INSERT INTO spell( id, name, description, icon, key, level_of_access, hero_id )
VALUES ( 1, 'Symbiote', 'Spawn and attach a Symbiote to a target ally or Structure. While active, Abathur controls the Symbiote, gaining access to new Abilities. The Symbiote is able to gain XP from nearby enemy deaths.', 'https://static.heroesofthestorm.com/gd/6f704aac5aa2f1cfad17ee130347fb3b/heroes/abathur/abilities/icons/symbiote_hexagon.png', 'Q', 1, 1 ),
       ( 2, 'Toxic Nest', 'Spawn a mine that becomes active after a short time. Deals 153 (+4% per level) damage and reveals the enemy for 4 seconds. Lasts 90 seconds.  Stores up to 3 charges.', 'https://static.heroesofthestorm.com/gd/6f704aac5aa2f1cfad17ee130347fb3b/heroes/abathur/abilities/icons/toxic-nest_hexagon.png', 'W', 1, 1 ),
       ( 3, 'Evolve Monstrosity', 'Turn an allied Minion or Locust into a Monstrosity. When enemy Minions near the Monstrosity die, it gains 2% Health and 2% Basic Attack damage, stacking up to 40 times.  The Monstrosity can be healed by Carapace and has the ability to Burrow to a visible location every 80 seconds.  Using Symbiote on the Monstrosity allows Abathur to control it, in addition to Symbiotes normal benefits.  This Ability can be reactivated to automatically cast Symbiote on his Monstrosity.', 'https://static.heroesofthestorm.com/gd/6f704aac5aa2f1cfad17ee130347fb3b/heroes/abathur/abilities/icons/evolve-monstrosity_hexagon.png', 'R', 1, 1 ),
       ( 4, 'Ultimate Evolution', 'Clone target allied Hero and control it for 20 seconds. Abathur has perfected the clone, granting it 20% Spell Power, 20% bonus Attack Damage, and 10% bonus Movement Speed. Cannot use their Heroic Ability.', 'https://static.heroesofthestorm.com/gd/6f704aac5aa2f1cfad17ee130347fb3b/heroes/abathur/abilities/icons/ultimate-evolution_hexagon.png', 'R', 1, 1 ),
       ( 5, 'Locust Strain', 'Spawns a Locust to attack down the nearest lane every 15 seconds. Locusts last for 16 seconds, have 316 (+4% per level) health and deal 46 (+4% per level) damage with each Basic Attack.', 'https://static.heroesofthestorm.com/gd/6f704aac5aa2f1cfad17ee130347fb3b/heroes/abathur/abilities/icons/locust-strain_hexagon.png', 'null', 1, 1 ),
       ( 6, 'Deep Tunnel', 'Quickly tunnel to a visible location', 'https://static.heroesofthestorm.com/gd/6f704aac5aa2f1cfad17ee130347fb3b/heroes/abathur/abilities/icons/deep-tunnel_hexagon.png', 'Z', 1, 1 ),
       ( 7, 'Stab', 'Shoots a spike towards target area that deals 119 (+4% per level) damage to the first enemy it contacts.', 'https://static.heroesofthestorm.com/gd/6f704aac5aa2f1cfad17ee130347fb3b/heroes/abathur/abilities/icons/stab_hexagon.png', 'Q', 1, 1 ),
       ( 8, 'Spike Burst', 'Deals 120 (+4% per level) damage to nearby enemies.', 'https://static.heroesofthestorm.com/gd/6f704aac5aa2f1cfad17ee130347fb3b/heroes/abathur/abilities/icons/spike-burst_hexagon.png', 'W', 1, 1 ),
       ( 9, 'Carapace', 'Shields the assisted ally for 150 (+4% per level). Allied Heroes are healed for 22 (+4% per level) Health per second while the Shield is active. Lasts for 6 seconds.', 'https://static.heroesofthestorm.com/gd/6f704aac5aa2f1cfad17ee130347fb3b/heroes/abathur/abilities/icons/carapace_hexagon.png', 'E', 1, 1 ),
       ( 10, 'Cancel Symbiote', 'Cancels the Symbiote ability. Releases your ally and returns to controlling Abathur.', 'https://static.heroesofthestorm.com/gd/6f704aac5aa2f1cfad17ee130347fb3b/heroes/abathur/abilities/icons/cancel-symbiote_hexagon.png', 'R', 1, 1 ),
       ( 11, 'Stab', 'Shoots a spike towards target area that deals 119 (+4% per level) damage to the first enemy it contacts.', 'https://static.heroesofthestorm.com/gd/6f704aac5aa2f1cfad17ee130347fb3b/heroes/abathur/abilities/icons/stab_hexagon.png', 'Q', 1, 1 ),
       ( 12, 'Spike Burst', 'Deals 120 (+4% per level) damage to nearby enemies.', 'https://static.heroesofthestorm.com/gd/6f704aac5aa2f1cfad17ee130347fb3b/heroes/abathur/abilities/icons/spike-burst_hexagon.png', 'W', 1, 1 ),
       ( 13, 'Carapace', 'Shields the assisted ally for 157 (+4% per level). Lasts for 8 seconds.', 'https://static.heroesofthestorm.com/gd/6f704aac5aa2f1cfad17ee130347fb3b/heroes/abathur/abilities/icons/carapace_hexagon.png', 'E', 1, 1 ),
       ( 14, 'Deep Tunnel', 'Order your Evolved Monstrosity to quickly tunnel to a visible location', 'https://static.heroesofthestorm.com/gd/6f704aac5aa2f1cfad17ee130347fb3b/heroes/abathur/abilities/icons/deep-tunnel_hexagon.png', 'Z', 1, 1 ),
       ( 15, 'Discord Strike', 'After a 0.5 second delay, enemies in front of Alarak take 175 (+4% per level) damage and are silenced for 1.5 seconds.', 'https://static.heroesofthestorm.com/gd/6f704aac5aa2f1cfad17ee130347fb3b/heroes/alarak/abilities/icons/discord-strike_hexagon.png', 'Q', 1, 2 ),
       ( 16, 'Telekinesis', 'Vector Targeting Create a force, pushing Alarak and all enemies hit from the targeted point towards the targeted direction. Deals 48 (+4% per level) damage to enemies.', 'https://static.heroesofthestorm.com/gd/6f704aac5aa2f1cfad17ee130347fb3b/heroes/alarak/abilities/icons/telekinesis_hexagon.png', 'W', 1, 2 ),
       ( 17, 'Lightning Surge', 'Deal 62 (+4% per level) damage to an enemy and an additional 100% damage to enemies between Alarak and the target. Restore 70 (+4% per level) health for each Hero hit.', 'https://static.heroesofthestorm.com/gd/6f704aac5aa2f1cfad17ee130347fb3b/heroes/alarak/abilities/icons/lightning-surge_hexagon.png', 'E', 1, 2 ),
       ( 18, 'Deadly Charge', 'After channeling, Alarak charges forward dealing 200 (+4% per level) damage to all enemies in his path. Distance is increased based on the amount of time channeled, up to 1.5 seconds.  Issuing a Move order while this is channeling will cancel it at no cost. Taking damage will interrupt the channeling.', 'https://static.heroesofthestorm.com/gd/6f704aac5aa2f1cfad17ee130347fb3b/heroes/alarak/abilities/icons/deadly-charge_hexagon.png', 'R', 1, 2 ),
       ( 19, 'Counter-Strike', 'Alarak targets an area and channels for 1 second, becoming Protected and Unstoppable. After, if he took damage from an enemy Hero, he sends a shockwave that deals 275 (+4% per level) damage.', 'https://static.heroesofthestorm.com/gd/6f704aac5aa2f1cfad17ee130347fb3b/heroes/alarak/abilities/icons/counter-strike_hexagon.png', 'R', 1, 2 ),
       ( 20, 'Sadism', 'Alaraks Ability damage and self-healing are increased by 100% against enemy Heroes.  Repeatable Quest: Takedowns increase Sadism by 3%, up to 30%. Sadism gained from Takedowns is lost on death.', 'https://static.heroesofthestorm.com/gd/6f704aac5aa2f1cfad17ee130347fb3b/heroes/alarak/abilities/icons/sadism_hexagon.png', 'null', 1, 2 ),
       ( 21, 'Impale', 'Deals 90 (+4% per level) damage. Stuns for 1 second.', 'https://static.heroesofthestorm.com/gd/6f704aac5aa2f1cfad17ee130347fb3b/heroes/anubarak/abilities/icons/impale_hexagon.png', 'Q', 1, 3 ),
       ( 22, 'Harden Carapace', 'Gain a Shield that grants 40 Spell Armor and absorbs 315 (+4% per level) damage over 3 seconds.', 'https://static.heroesofthestorm.com/gd/6f704aac5aa2f1cfad17ee130347fb3b/heroes/anubarak/abilities/icons/harden-carapace_hexagon.png', 'W', 1, 3 ),
       ( 23, 'Burrow Charge', 'Burrow to the target location, dealing 91 (+4% per level) damage and briefly stunning enemies in a small area upon surfacing, slowing them by 25% for 2.5 seconds.  Burrow Charge can be reactivated to surface early.', 'https://static.heroesofthestorm.com/gd/6f704aac5aa2f1cfad17ee130347fb3b/heroes/anubarak/abilities/icons/burrow-charge_hexagon.png', 'E', 1, 3 ),
       ( 24, 'Locust Swarm', 'Deal 62 (+4% per level) damage per second to nearby enemies. Each enemy damaged restores 21 (+4% per level) Health. Lasts 6 seconds.', 'https://static.heroesofthestorm.com/gd/6f704aac5aa2f1cfad17ee130347fb3b/heroes/anubarak/abilities/icons/locust-swarm_hexagon.png', 'R', 1, 3 ),
       ( 25, 'Cocoon', 'Wraps target enemy Hero in a cocoon, rendering them unable to act or be targeted for 7 seconds. Allies of the Hero can attack the cocoon to break it and free them early.', 'https://static.heroesofthestorm.com/gd/6f704aac5aa2f1cfad17ee130347fb3b/heroes/anubarak/abilities/icons/cocoon_hexagon.png', 'R', 1, 3 ),
       ( 26, 'Scarab Host', 'Using an Ability spawns a Beetle which lasts for 8 seconds, attacking nearby enemies for 20 (+4% per level) damage.', 'https://static.heroesofthestorm.com/gd/6f704aac5aa2f1cfad17ee130347fb3b/heroes/anubarak/abilities/icons/scarab-host_hexagon.png', 'null', 1, 3 ),
       ( 27, 'Blade Dash', 'Dash forward and deal 57 (+4% per level) damage to enemies, then return and deal 171 (+4% per level) damage. Every enemy hit reduces the cooldown on Shield Overload by 1 second, and Heroes by 2 seconds.', 'https://static.heroesofthestorm.com/gd/6f704aac5aa2f1cfad17ee130347fb3b/heroes/artanis/abilities/icons/blade-dash_hexagon.png', 'Q', 1, 4 ),
       ( 28, 'Twin Blades', 'Artaniss next Basic Attack immediately causes him to charge a short distance and strike the enemy 2 times.', 'https://static.heroesofthestorm.com/gd/6f704aac5aa2f1cfad17ee130347fb3b/heroes/artanis/abilities/icons/twin-blades_hexagon.png', 'W', 1, 4 ),
       ( 29, 'Phase Prism', 'Fire a Phase Prism that deals 66 (+4% per level) damage to the first Hero hit and swaps Artaniss position with theirs. Can be used during Blade Dash.', 'https://static.heroesofthestorm.com/gd/6f704aac5aa2f1cfad17ee130347fb3b/heroes/artanis/abilities/icons/phase-prism_hexagon.png', 'E', 1, 4 ),
       ( 30, 'Purifier Beam', 'Target an enemy Hero with an orbital beam from the Spear of Adun, dealing 184 (+4% per level) damage per second for 8 seconds. The beam will chase the target as they move.  Unlimited range.', 'https://static.heroesofthestorm.com/gd/6f704aac5aa2f1cfad17ee130347fb3b/heroes/artanis/abilities/icons/purifier-beam_hexagon.png', 'R', 1, 4 ),
       ( 31, 'Suppression Pulse', 'Fire a large area pulse from the Spear of Adun, dealing 114 (+4% per level) damage and Blinding enemies for 4 seconds. Unlimited range.', 'https://static.heroesofthestorm.com/gd/6f704aac5aa2f1cfad17ee130347fb3b/heroes/artanis/abilities/icons/suppression-pulse_hexagon.png', 'R', 1, 4 ),
       ( 32, 'Shield Overload', 'After taking damage while below 75% Health, Artanis gains a 375 (+4% per level) Shield for 5 seconds. Basic Attacks lower the cooldown of Shield Overload by 4 seconds.', 'https://static.heroesofthestorm.com/gd/6f704aac5aa2f1cfad17ee130347fb3b/heroes/artanis/abilities/icons/shield-overload_hexagon.png', 'null', 1, 4 ),
